import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      secretWord: "HELLO", // Kata rahasia yang harus ditebak
      guessedLetters: new Set(), // Set huruf yang sudah ditebak
      remainingAttempts: 6 // Jumlah kesempatan tersisa
    };
  }

  // Fungsi untuk menebak huruf
  guessLetter = (letter) => {
    if (!this.state.guessedLetters.has(letter)) {
      this.setState(prevState => ({
        guessedLetters: prevState.guessedLetters.add(letter),
        remainingAttempts: prevState.secretWord.includes(letter)
          ? prevState.remainingAttempts // Jika huruf benar, tidak mengurangi kesempatan
          : prevState.remainingAttempts - 1 // Jika huruf salah, kurangi kesempatan
      }));
    }
  }

  // Fungsi untuk merender teks kata rahasia
  renderSecretWord = () => {
    const { secretWord, guessedLetters } = this.state;
    return secretWord.split('').map((letter, index) => (
      <span key={index} className="letter">
        {guessedLetters.has(letter) ? letter : '_'}
      </span>
    ));
  }

  // Fungsi untuk merender tombol huruf
  renderLetterButtons = () => {
    const { guessedLetters } = this.state;
    const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    return alphabet.split('').map((letter, index) => (
      <button
        key={index}
        disabled={guessedLetters.has(letter)}
        onClick={() => this.guessLetter(letter)}
      >
        {letter}
      </button>
    ));
  }

  render() {
    const { remainingAttempts } = this.state;
    const gameOver = remainingAttempts === 0;
    const gameWon = !this.renderSecretWord().includes('_');

    return (
      <div className="App">
        <h1>Hangman</h1>
        <div className="secret-word">{this.renderSecretWord()}</div>
        <div className="letter-buttons">{this.renderLetterButtons()}</div>
        <div className="remaining-attempts">Remaining Attempts: {remainingAttempts}</div>
        {gameOver && <div className="game-over">Game Over! The secret word was HELLO.</div>}
        {gameWon && <div className="game-won">Congratulations! You guessed the secret word.</div>}
      </div>
    );
  }
}

export default App;
