import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ball: {
        x: 50,
        y: 50,
        dx: 2,
        dy: 2,
        radius: 10
      },
      paddle: {
        width: 100,
        height: 10,
        x: 0
      },
      canvasWidth: 800,
      canvasHeight: 600
    };
    this.canvasRef = React.createRef();
  }

  componentDidMount() {
    this.updateCanvas();
  }

  updateCanvas = () => {
    const ctx = this.canvasRef.current.getContext('2d');
    const { canvasWidth, canvasHeight, ball, paddle } = this.state;

    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Draw ball
    ctx.beginPath();
    ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
    ctx.fillStyle = '#0095DD';
    ctx.fill();
    ctx.closePath();

    // Draw paddle
    ctx.beginPath();
    ctx.rect(paddle.x, canvasHeight - paddle.height, paddle.width, paddle.height);
    ctx.fillStyle = '#0095DD';
    ctx.fill();
    ctx.closePath();

    // Move ball
    let nextX = ball.x + ball.dx;
    let nextY = ball.y + ball.dy;
    if (nextX < ball.radius || nextX > canvasWidth - ball.radius) {
      ball.dx *= -1;
    }
    if (nextY < ball.radius || nextY > canvasHeight - ball.radius) {
      ball.dy *= -1;
    }
    this.setState(prevState => ({
      ball: { ...prevState.ball, x: nextX, y: nextY }
    }));

    requestAnimationFrame(this.updateCanvas);
  }

  handleMouseMove = (e) => {
    const canvasRect = this.canvasRef.current.getBoundingClientRect();
    const mouseX = e.clientX - canvasRect.left;
    const paddleX = mouseX - this.state.paddle.width / 2;
    this.setState(prevState => ({
      paddle: { ...prevState.paddle, x: paddleX }
    }));
  }

  render() {
    const { canvasWidth, canvasHeight } = this.state;
    return (
      <div className="App">
        <canvas
          ref={this.canvasRef}
          width={canvasWidth}
          height={canvasHeight}
          onMouseMove={this.handleMouseMove}
        />
      </div>
    );
  }
}

export default App;
