import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [
        {
          question: "Siapakah presiden pertama Indonesia?",
          options: ["Soekarno", "Soeharto", "Jokowi", "Gus Dur"],
          correctAnswer: "Soekarno"
        },
        {
          question: "Berapa banyak planet di tata surya kita?",
          options: ["6", "7", "8", "9"],
          correctAnswer: "8"
        },
        {
          question: "Apa ibu kota Indonesia?",
          options: ["Jakarta", "Surabaya", "Bandung", "Yogyakarta"],
          correctAnswer: "Jakarta"
        }
      ],
      currentQuestionIndex: 0,
      selectedOption: null,
      score: 0,
      showResult: false
    };
  }

  // Fungsi untuk memilih opsi jawaban
  selectOption = (option) => {
    this.setState({ selectedOption: option });
  }

  // Fungsi untuk memeriksa jawaban yang dipilih
  checkAnswer = () => {
    const { questions, currentQuestionIndex, selectedOption, score } = this.state;
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedOption === currentQuestion.correctAnswer) {
      this.setState({ score: score + 1 });
    }
    this.moveToNextQuestion();
  }

  // Fungsi untuk beralih ke pertanyaan berikutnya
  moveToNextQuestion = () => {
    const { currentQuestionIndex, questions } = this.state;
    if (currentQuestionIndex < questions.length - 1) {
      this.setState(prevState => ({
        currentQuestionIndex: prevState.currentQuestionIndex + 1,
        selectedOption: null
      }));
    } else {
      this.setState({ showResult: true });
    }
  }

  // Fungsi untuk merender pertanyaan dan opsi jawaban
  renderQuestion = () => {
    const { questions, currentQuestionIndex, selectedOption } = this.state;
    const currentQuestion = questions[currentQuestionIndex];
    return (
      <div>
        <h2>{currentQuestion.question}</h2>
        <ul>
          {currentQuestion.options.map((option, index) => (
            <li
              key={index}
              onClick={() => this.selectOption(option)}
              className={selectedOption === option ? 'selected' : ''}
            >
              {option}
            </li>
          ))}
        </ul>
        <button onClick={this.checkAnswer} disabled={!selectedOption}>Next</button>
      </div>
    );
  }

  // Fungsi untuk merender skor akhir
  renderResult = () => {
    const { score } = this.state;
    return (
      <div>
        <h2>Game Over!</h2>
        <p>Your Score: {score}</p>
      </div>
    );
  }

  render() {
    const { showResult } = this.state;
    return (
      <div className="App">
        <h1>Quiz Game</h1>
        {showResult ? this.renderResult() : this.renderQuestion()}
      </div>
    );
  }
}

export default App;
